import React, { useEffect, useState } from "react";
import  { Auth, Hub } from "aws-amplify";
import './components/Login.css';
import './components/Signup';
import email from "./image/email.png";
import pass from "./image/lock.png";
import logo from "./image/logo.png";
import useri from "./image/user.png";
// import Login from './components/Login';
// import Signup from './components/Signup';


const initialFormState = {
  username: "",
  password: "",
  email: "",
  authCode: "",
  formType: "login",
};


function App() {
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  function validatePassword() {
    if (password !== confirmPassword) {
      document.getElementById('confirm_password').setCustomValidity("Passwords Don't Match");
    } else {
      document.getElementById('confirm_password').setCustomValidity('');
    }
  }


  const [formState, updateFormState] = useState(initialFormState);

  const [user, updateUser] = useState(null);

  const checkUser = async () => {
    try {
      const user = await Auth.currentAuthenticatedUser();

      updateUser(user);

      console.log("got user", user);

      updateFormState(() => ({ ...formState, formType: "loggedIn" }));
    } catch (err) {
      console.log("checkUser error", err);
      updateFormState(() => ({ ...formState, formType: "login" }));
    }
  };

  // Skip this if you're not using Hub. You can call updateFormState function right
  // after the Auth.signOut() call in the button.
  const setAuthListener = async () => {
    Hub.listen("auth", (data) => {
      switch (data.payload.event) {
        case "signOut":
          console.log(data);

          updateFormState(() => ({
            ...formState,
            formType: "login",
          }));

          break;
        case "login":
          console.log(data);

          break;
      }
    });
  };

  useEffect(() => {
    checkUser();
    setAuthListener();
  }, []);

  const onChange = (e) => {
    e.persist();
    updateFormState(() => ({ ...formState, [e.target.name]: e.target.value }));
  };

  const { formType } = formState;

  const signUp = async () => {
    const { username, email, password } = formState;

    await Auth.signUp({ username, password, attributes: { email } });

    updateFormState(() => ({ ...formState, formType: "confirmSignUp" }));
  };

  const confirmSignUp = async () => {
    const { email, authCode } = formState;

    await Auth.confirmSignUp(email, authCode);

    updateFormState(() => ({ ...formState, formType: "login" }));
  };

  const login = async () => {
    const { email, password } = formState;

    await Auth.login(email, password);

    updateFormState(() => ({ ...formState, formType: "loggedIn" }));
  };

  // console.log(formType);

  return (
    <>
      {formType === "signUp" && (
      //   <div className='s_img'>
      //   <meta name="viewport" content="width=device-width,initial-scale=1"/>

      // {/* <div className='s_suvi'>
      //   Space Energy Systems
      // </div>  */}
      // <div className="s_main" >
      //  <div className="s_intro">
      //  <div className='s_ses'>Space Energy Systems</div>
      //     <div><h2>EVERYTHING EASY AND ECONOMICAL</h2>
      //     <p>Construction application that gets you easy access to building information, create plan, schedule, resources, material and related services at your fingertips.</p>
      //     </div>
      //  </div>
      //  <div className='s_frame'>
      //  <div className="s_sub-main">

      //      <div >
      //      <div className='s_title-logo'>
      //      <img src={logo} alt="logo" className="s_logo-pic"/>
      //      </div> 
      //      <div>
      //       <form name='form2' autocomplete="off">
      //       <div>
      //         <img src={useri} alt="user" className="s_name-pic"/>
      //          <input type="text" name="username" onChange={onChange} placeholder="Name" className="s_name" required/><br></br>
      //       </div>
      //        <div className="s_second-input">
      //          <img src={email} alt="email" className="s_email-pic"/>
      //          <input type="email" name="email"onChange={onChange} placeholder="Email" className="s_name" required/><br></br>
      //        </div>
      //        <div className="s_second-input">
      //          <img src={pass} alt="pass" className="s_email-pic"/>
      //          <input type="password" name="password" id='password' placeholder="4-digit pin" pattern='^\d{4}$' className="s_name" value={password} onChange={(e) => setPassword(e.target.value)} required/><br></br>
      //        </div>
      //        <div className="s_second-input">
      //          <img src={pass} alt="pass" className="s_email-pic"/>
      //          <input type="password" onChange={onChange} id='confirm_password' placeholder="Verify pin" pattern='^\d{4}$' className="s_name" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} onKeyUp={validatePassword} required/><br></br>
      //        </div>
      //       <br></br>
      //       <button onClick={signUp} >Signup</button>       
      //        <p>
      //           <a>Already have an account?</a>
      //           <br></br>
      //           <a onClick={() =>updateFormState(() => ({
      //           ...formState,
      //           formType: "login",
      //           }))
      //           } >Login</a>
      //         </p>
      //         <br></br>             
      //         </form> 
      //      </div>      
      //      </div>
      //   </div> 
      //   </div>
      // </div>
      // </div>


        <div>
          <input name="username" onChange={onChange} placeholder="username" />
          <input
            name="password"
            type="password"
            onChange={onChange}
            placeholder="password"
          />
          <input name="email" onChange={onChange} placeholder="email" />

          <button onClick={signUp}>Sign Up</button>

          <p>Already signed up?</p>

          <button
            onClick={() =>
              updateFormState(() => ({
                ...formState,
                formType: "login",
              }))
            }
          >
            Sign In instead
          </button>
        </div>
      )}

      {formType === "confirmSignUp" && (
        <div>
          <input
            name="authCode"
            onChange={onChange}
            placeholder="confirm auth code"
          />
          <button onClick={confirmSignUp}>Confirm Sign up</button>
        </div>
      )}

      {formType === "login" && (
         <div className='img'>
         <meta name="viewport" content="width=device-width,initial-scale=1"/>
       <div className="main" >
        <div className="intro">
           <div className='ses'>Space Energy Systems</div>
           <div><h2>EVERYTHING EASY AND ECONOMICAL</h2>
           <p>Construction application that gets you easy access to building information, create plan, schedule, resources, material and related services at your fingertips.</p>
           </div>
        </div>

       <div className='frame'>
        <div className="sub-main">

            <div >
