import React, { useEffect, useState } from "react";
import  { Auth, Hub } from "aws-amplify";
import './components/Login.css';
import email from "./../image/email.png";
import pass from "./../image/lock.png";
import logo from "./../image/logo.png";
// import Login from './components/Login';
// import Signup from './components/Signup';


const initialFormState = {
  username: "",
  password: "",
  email: "",
  authCode: "",
  formType: "login",
};

function App() {
  const [formState, updateFormState] = useState(initialFormState);

  const [user, updateUser] = useState(null);

  const checkUser = async () => {
    try {
      const user = await Auth.currentAuthenticatedUser();

      updateUser(user);

      console.log("got user", user);

      updateFormState(() => ({ ...formState, formType: "signedIn" }));
    } catch (err) {
      console.log("checkUser error", err);
      updateFormState(() => ({ ...formState, formType: "login" }));
    }
  };

  // Skip this if you're not using Hub. You can call updateFormState function right
  // after the Auth.signOut() call in the button.
  const setAuthListener = async () => {
    Hub.listen("auth", (data) => {
      switch (data.payload.event) {
        case "signOut":
          console.log(data);

          updateFormState(() => ({
            ...formState,
            formType: "login",
          }));

          break;
        case "login":
          console.log(data);

          break;
      }
    });
  };

  useEffect(() => {
    checkUser();
    setAuthListener();
  }, []);

  const onChange = (e) => {
    e.persist();
    updateFormState(() => ({ ...formState, [e.target.name]: e.target.value }));
  };

  const { formType } = formState;

  const signUp = async () => {
    const { username, email, password } = formState;

    await Auth.signUp({ username, password, attributes: { email } });

    updateFormState(() => ({ ...formState, formType: "confirmSignUp" }));
  };

  const confirmSignUp = async () => {
    const { username, authCode } = formState;

    await Auth.confirmSignUp(username, authCode);

    updateFormState(() => ({ ...formState, formType: "login" }));
  };

  const login = async () => {
    const { username, password } = formState;

    await Auth.login(username, password);

    updateFormState(() => ({ ...formState, formType: "signedIn" }));
  };

  // console.log(formType);

  return (
    <>
      <h1>App</h1>

      {formType === "signUp" && (
        <div>
          <input name="username" onChange={onChange} placeholder="username" />
          <input
            name="password"
            type="password"
            onChange={onChange}
            placeholder="password"
          />
          <input name="email" onChange={onChange} placeholder="email" />

          <button onClick={signUp}>Sign Up</button>

          <p>Already signed up?</p>

          <button
            onClick={() =>
              updateFormState(() => ({
                ...formState,
                formType: "login",
              }))
            }
          >
            Sign In instead
          </button>
        </div>
      )}

      {formType === "confirmSignUp" && (
        <div>
          <input
            name="authCode"
            onChange={onChange}
            placeholder="cnfirm auth code"
          />
          <button onClick={confirmSignUp}>Confirm Sign up</button>
        </div>
      )}

      {formType === "login" && (
         <div className='img'>
         <meta name="viewport" content="width=device-width,initial-scale=1"/>
       <div className="main" >
        <div className="intro">
           <div className='ses'>Space Energy Systems</div>
           <div><h2>EVERYTHING EASY AND ECONOMICAL</h2>
           <p>Construction application that gets you easy access to building information, create plan, schedule, resources, material and related services at your fingertips.</p>
           </div>
        </div>

       <div className='frame'>
        <div className="sub-main">

            <div >
            <div className='title-logo'>
            <img src={logo} alt="logo" className="logo-pic"/>
            </div>
            <div>
            <form name='form1' autocomplete="off" >

              <div>
                <img src={email} alt="email"  className="email-pic"/>
                <input type="email" onChange={e => {e.persist(); updateFormState(e)}} name="email" placeholder="Email" className="name" required/><br></br>
              </div>
              <div className="second-input">
                <img src={pass} alt="pass" className="email-pic"/>
                <input type="password" onChange={e => {e.persist(); updateFormState(e);}} name="password" placeholder="4-digit pin" className="name" pattern='^\d{4}$' required/><br></br>
              </div>
             <br></br>
             <button onClick={login} >Login</button>

             </form>
          </div> 
              <p >
                 <a href="#" className='link'>Forgot pin?</a> 
                 <br></br>
                 <a href="/signup" >Sign Up</a>
               </p>
               <br></br>


            </div>


            </div>
         </div> 

       </div>

       </div>


      //   <div>
      //     <input name="username" onChange={onChange} placeholder="username" />
      //     <input
      //       name="password"
      //       type="password"
      //       onChange={onChange}
      //       placeholder="password"
      //     />
      //     <button onClick={login}>Sign In</button>

      //     <p>No account yet?</p>

      //     <button
      //       onClick={() =>
      //         updateFormState(() => ({
      //           ...formState,
      //           formType: "signUp",
      //         }))
      //       }
      //     >
      //       Sign Up now
      //     </button>
      //   </div>
      // )}

      // {formType === "signedIn" && (
      //   <div>
      //     <h2>
      //       Welcome the app, {user.username} ({user.attributes.email})!
      //     </h2>

      //     <button
      //       onClick={() => {
      //         Auth.signOut();
      //       }}
      //     >
      //       Sign out
      //     </button>
      //   </div>
      )}

      <hr />
    </>
  );
}

export default App;


