import React, { useEffect, useState } from "react";
import  { Auth, Hub } from "aws-amplify";
import './components/Login.css';
import './components/Signup';
import email from "./image/email.png";
import pass from "./image/lock.png";
import logo from "./image/logo.png";
import useri from "./image/user.png";
// import Login from './components/Login';
// import Signup from './components/Signup';


const initialFormState = {
  username: "",
  password: "",
  email: "",
  authCode: "",
  formType: "login",
};


function App() {
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  function validatePassword() {
    if (password !== confirmPassword) {
      document.getElementById('confirm_password').setCustomValidity("Passwords Don't Match");
    } else {
      document.getElementById('confirm_password').setCustomValidity('');
    }
  }


  const [formState, updateFormState] = useState(initialFormState);

  const [user, updateUser] = useState(null);

  const checkUser = async () => {
    try {
      const user = await Auth.currentAuthenticatedUser();

      updateUser(user);

      console.log("got user", user);

      updateFormState(() => ({ ...formState, formType: "loggedIn" }));
    } catch (err) {
      console.log("checkUser error", err);
      updateFormState(() => ({ ...formState, formType: "login" }));
    }
  };

  // Skip this if you're not using Hub. You can call updateFormState function right
  // after the Auth.signOut() call in the button.
  const setAuthListener = async () => {
    Hub.listen("auth", (data) => {
      switch (data.payload.event) {
        case "signOut":
          console.log(data);

          updateFormState(() => ({
            ...formState,
            formType: "login",
          }));

          break;
        case "login":
          console.log(data);

          break;
      }
    });
  };

  useEffect(() => {
    checkUser();
    setAuthListener();
  }, []);

  const onChange = (e) => {
    e.persist();
    updateFormState(() => ({ ...formState, [e.target.name]: e.target.value }));
  };

  const { formType } = formState;

  const signUp = async () => {
    const { username, email, password } = formState;

    await Auth.signUp({ username, password, attributes: { email } });

    updateFormState(() => ({ ...formState, formType: "confirmSignUp" }));
  };

  const confirmSignUp = async () => {
    const { email, authCode } = formState;

    await Auth.confirmSignUp(email, authCode);

    updateFormState(() => ({ ...formState, formType: "login" }));
  };

  const login = async () => {
    const { email, password } = formState;

    await Auth.login(email, password);

    updateFormState(() => ({ ...formState, formType: "loggedIn" }));
  };

  // console.log(formType);

  return (
    <>
      {formType === "signUp" && (
        <div className='s_img'>
        <meta name="viewport" content="width=device-width,initial-scale=1"/>

      {/* <div className='s_suvi'>
        Space Energy Systems
      </div>  */}
      <div className="s_main" >
       <div className="s_intro">
