import './Login.css';
import email from "./../image/email.png";
import pass from "./../image/lock.png";
import React from "react";
import logo from "./../image/logo.png"
function Validate(inputText)
{
var mailformat = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;
if(inputText.value.match(mailformat))
{
alert("Valid email address!");
Login.focus();
return true;
}
else
{
alert("You have entered an invalid email address!");
Login.focus();
return false;
}
}

function Login() {
  return (
    // <body onLoad='Login.focus()'>
    <body>
    <div className='img'>
      <meta name="viewport" content="width=device-width,initial-scale=1"/>

    <div className='suvi'>
      Space Energy Systems
    </div> 
    <div className="main" >
     <div className="intro">
      
        <h1>EVERYTHING EASY AND ECONOMICAL</h1>
        <p>Construction application that gets you easy access to building information, create plan, schedule, resources, material and related services at your fingertips.</p>
      
     </div>
     
    <div className='frame'>
     <div className="sub-main">
       
         <div >
         <div className='title-logo'>
         <img src={logo} alt="logo" className="logo-pic"/>
         </div>
         <div>
         <form name='form1' action='#'>
          
           <div>
             <img src={email} alt="email" className="email-pic"/>
             <input type="text" placeholder="Email" className="name"/><br></br>
           </div>
           <div className="second-input">
             <img src={pass} alt="pass" className="email-pic"/>
             <input type="password" placeholder="4-digit pin" className="name"/><br></br>
           </div>
          <br></br>
          {/* <div className='button'> */}
          <button onclick="Validate(Login)">Login</button>
          {/* <input type="submit" name="Login" value="Login" onclick="Validate(Login)"/>
          </div> */}
          </form>
       </div> 
           <p >
              <a href="#" className='link'>Forgot pin?</a> 
              <br></br>
              <a href="/signup" >Sign Up</a>
            </p>
            <br></br>
           
           
         </div>
       
       
         </div>
      </div> 
      
    </div>
    
    </div>
    </body>
  );
}
export default Login;
